mapper.insert:
insert into tbl_restreply
	(rno
	, bno
	, replytext
	, replyer
	, regdate
	, updatedate
	, rgroup
	, rseq
	, rlevel
	, delete_yn
	)
values
	(seq_reply_rno.nextval
	, #{bno}
	, #{replytext}
	, #{replyer}
	, #{regdate}
	, #{updatedate}
	, #{rgroup}
	, #{rseq}
	, #{rlevel}
	, #{delete_yn}
	)
