mapper.select:
select * from tbl_restreply
where bno = #{bno}
order by rgroup, rseq

mapper.insert:
insert into tbl_restreply
	(rno
	, bno
	, replytext
	, replyer
	, rgroup
	, rseq
	, rlevel
	)
values
	(seq_reply_rno.nextval
	, #{bno}
	, #{replytext}
	, #{replyer}
	, #{rgroup}
	, #{rseq}
	, #{rlevel}
	)
