mapper.select:
select * from tbl_restreply
where bno = #{bno}
order by rgroup, rseq

mapper.insert:
insert into tbl_restreply
	(rno
	, bno
	, replytext
	, replyer
	, rgroup
	, userid
	)
values
	(seq_restreply_rno.nextval
	, #{bno}
	, #{replytext}
	, #{replyer}
	, seq_restreply_rno.currval
	, #{userid}
	)

insert into tbl_restreply
	(rno
	, bno
	, replytext
	, replyer
	, rgroup
	, rseq
	, rlevel
	, unickname
	, userid
	)
values
	(seq_reply_rno.nextval
	, #{bno}
	, #{replytext}
	, #{replyer}
	, #{rgroup}
	, #{rseq}
	, 1
	, #{unickname}
	, #{userid}
	)

mapper.select:
select max(rseq) from tbl_restreply
where bno = #{bno}
and rgroup = #{rgroup}

select count(*) from tbl_restreply
where bno = #{bno}
and delete_yn = 'N'

select count(*) from tbl_restreply
where rgroup = #{rno}
and delete_yn = 'N'

mapper.delete:
update tbl_restreply
set delete_yn = 'Y'
where rno = #{rno}
