mapper.select:
select * from tbl_userreply
where bno = #{bno}
order by rgroup, rseq

select max(rseq) from tbl_userreply
where bno = #{bno}
and rgroup = #{rgroup}

select count(*) from tbl_userreply
where bno = #{bno}
and delete_yn = 'N'

mapper.update:
update tbl_userreply
set delete_yn = 'Y'
where rno = #{rno}

mapper.select:
select count(*) from tbl_userreply
where rgroup = #{rno}
and delete_yn = 'N'
